package ezo.mp.domain;

public class Cart {

	private String cartCode;
	private String productCode;
	private String productEA;
	private String userid;
	private int amount;

	public Cart(String productCode, String productEA, String userid, int amount) {

		this.productCode = productCode;
		this.productEA = productEA;
		this.userid = userid;
		this.amount = amount;
	}

	public Cart() {
		// TODO Auto-generated constructor stub
	}

	@Override
	public String toString() {
		return "Cart [cartCode=" + cartCode + ", productCode=" + productCode
				+ ", productEA=" + productEA + ", userid=" + userid
				+ ", amount=" + amount + "]";
	}

	public String getCartCode() {
		return cartCode;
	}

	public void setCartCode(String cartCode) {
		this.cartCode = cartCode;
	}

	public String getProductCode() {
		return productCode;
	}

	public void setProductCode(String productCode) {
		this.productCode = productCode;
	}

	public String getProductEA() {
		return productEA;
	}

	public void setProductEA(String productEA) {
		this.productEA = productEA;
	}

	public String getUserid() {
		return userid;
	}

	public void setUserid(String userid) {
		this.userid = userid;
	}

	public int getAmount() {
		return amount;
	}

	public void setAmount(int amount) {
		this.amount = amount;
	}

}
